(185, 'tgwzw10lr01r49ngzoxu', 'pgv6xcbyc3dexl2xjrq9+jt5c0q553u2cqn3yqhd7+', '<p><span style="font-size:20px"><strong>తెగులు</strong> <strong>నియంత్రణ</strong> <strong>&ndash;</strong> <strong>నివారణ &nbsp;పద్దతులు </strong></span></p>\r\n\r\n<ul>\r\n	<li><span style="font-size:18px"><strong>రసాయన</strong> <strong>పద్దతులు</strong></span></li>\r\n</ul>\r\n\r\n<ul style="list-style-type:circle">\r\n	<li>విత్తన శుద్ధి&nbsp; - కిలో విత్తనాలకు ౩ గ్రాముల కాప్తాన్ లేదా థైరాం కలిపి విత్తన శుద్ధి చేయాలి.</li>\r\n	<li>0. 25% మాన్కోజేబ్ లేదా 0. 1%&nbsp; కార్బెండైజిం పంట మీద చల్లాలి</li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style="font-size:18px"><strong>జీవ</strong> <strong>పద్దతులు</strong></span>\r\n\r\n	<ul style="list-style-type:circle">\r\n		<li>తెగులు సోకిన పంట అవశేషాలను, ఫంగస్ కు ఆతిధ్యమిచ్చే ఇతర ఆతిదేయులను తొలగించాలి&nbsp;</li>\r\n		<li>తగులు నిరోధక వంగడాలని (ఎస్.పి.వి 162, సి. ఎస్.వి. 17వంటి రకాలు) వాడటం</li>\r\n	</ul>\r\n	</li>\r\n</ul>\r\n\r\n<p style="margin-left:108.0pt">&nbsp;</p>\r\n'),
(186, 'tgwzw10lr01r49ngzoxu', 'pgv6xcbyc3dexl2xjrq9+jt5c0q553u2cqn3yqhd7+', '<p><span style="font-size:20px"><strong>తెగులు</strong> <strong>నియంత్రణ</strong> <strong>&ndash;</strong> <strong>నివారణ &nbsp;పద్దతులు </strong></span></p>\r\n\r\n<ul>\r\n	<li><span style="font-size:18px"><strong>రసాయన</strong> <strong>పద్దతులు</strong></span></li>\r\n</ul>\r\n\r\n<ul style="list-style-type:circle">\r\n	<li>విత్తన శుద్ధి&nbsp; - కిలో విత్తనాలకు ౩ గ్రాముల కాప్తాన్ లేదా థైరాం కలిపి విత్తన శుద్ధి చేయాలి.</li>\r\n	<li>0. 25% మాన్కోజేబ్ లేదా 0. 1%&nbsp; కార్బెండైజిం పంట మీద చల్లాలి</li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style="font-size:18px"><strong>జీవ</strong> <strong>పద్దతులు</strong></span>\r\n\r\n	<ul style="list-style-type:circle">\r\n		<li>తెగులు సోకిన పంట అవశేషాలను, ఫంగస్ కు ఆతిధ్యమిచ్చే ఇతర ఆతిదేయులను తొలగించాలి&nbsp;</li>\r\n		<li>తగులు నిరోధక వంగడాలని (ఎస్.పి.వి 162, సి. ఎస్.వి. 17వంటి రకాలు) వాడటం</li>\r\n	</ul>\r\n	</li>\r\n</ul>\r\n\r\n<p style="margin-left:108.0pt">&nbsp;</p>\r\n'),
(187, '981gemift3w4agpqeb0k', 'jt5c0q553u2cqn3yqhd7+', '<p><span style="font-size:20px"><strong>తెగులు</strong> <strong>నియంత్రణ</strong> <strong>&ndash;</strong> </span><strong><span style="font-size:20px">నివారణ &nbsp;పద్దతులు</span> </strong></p>\r\n\r\n<ul>\r\n	<li><span style="font-size:18px"><strong>రసాయన</strong> <strong>పద్దతులు</strong></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li>పంట మీద 0. 25% మాన్కోజేబ్&nbsp; చల్లాలి.</li>\r\n	<li>హెక్టారుకు 25 కిలోల సల్ఫార్ చల్లాలి</li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style="font-size:20px"><strong>జీవ</strong> <strong>పద్దతులు</strong></span></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul style="list-style-type:circle">\r\n	<li>ఫంగస్ వ్యాప్తికి దోహదం చేసే ప్రత్యామ్నాయ అతిధి( అక్సాలిస్ కార్నికులేతా) లేకుండా చేయాలి.</li>\r\n	<li>తెగులు నిరోధక వంగడాలను (సి.ఎస్.హెచ్. 5, సి.ఎస్.వి 17 వంటివి) వాడాలి.</li>\r\n</ul>\r\n\r\n<p style="margin-left:108.0pt">&nbsp;</p>\r\n'),
(188, 'j89tj2xwym1w9eyd7iut', '', '<p><span style="font-size:20px"><strong>సస్య రక్షణ :</strong></span></p>\r\n\r\n<p>మెటాలాక్సిల్ 4 గ్రా. 1 కిలో విత్తనానికి కలిపి&nbsp; విత్తన శుద్ధి చేయాలి. మరియు మెటాలాక్సిల్ యం.జడ్. 2 గ్రా. లేక మాన్కోజేట్ 2.5 గ్రా. లీటరు నీటికి కలిపి పిచికారి చేసి ఈ తెగులును అరికట్టవచ్చును.&nbsp;</p>\r\n'),
(189, 'axno7il65gcqkmy9tkh3', 'pgv6xcbyc3dexl2xjrq9+jt5c0q553u2cqn3yqhd7+', '<p><span style="font-size:20px"><strong>తెగులు</strong> <strong>నియంత్రణ</strong> <strong>&ndash;</strong> </span><strong><span style="font-size:20px">నివారణ &nbsp;పద్దతులు</span> </strong></p>\r\n\r\n<ul>\r\n	<li><span style="font-size:20px"><strong>రసాయన</strong> <strong>పద్దతులు</strong></span></li>\r\n</ul>\r\n\r\n<p style="margin-left:72.0pt">&nbsp;</p>\r\n\r\n<ul>\r\n	<li>విత్తన శుద్ధి&nbsp; - కిలో విత్తనాలకు 4 గ్రాముల కాప్తాన్ లేదా థైరాం కలిపి విత్తనాలను శుద్ధి చేయాలి.</li>\r\n	<li>కంకులు కనపడగానే ( 5 &ndash; 10% పూత దశలో) జిరాం లేదా 0.2%&nbsp; మాన్కోజేబ్ చల్లాలి.&nbsp; తరువాత మరోసారి 50% పూత దశలో చల్లాలి. ఇంకా అవసరం అయితే వారం రోజుల తరువాత మరోసారి చల్లాలి.</li>\r\n	<li>విత్తన ఉత్పత్తి&nbsp; క్షేత్రాలలో &shy;ప్రోపికోనజోల్ లేదా టేబుకోనాజోల్ వంటి మందులు వాడి తెగులును నియంత్రించడం అన్నది ఖర్చు తక్కువ పని.</li>\r\n</ul>\r\n\r\n<p>&nbsp;&nbsp;</p>\r\n\r\n<ul>\r\n	<li><span style="font-size:20px"><strong>జీవ</strong> <strong>పద్దతులు</strong></span>\r\n\r\n	<ul>\r\n		<li>వానలు ఎక్కువ పడే సమయం లేదా తేమ ఎక్కువగా ఉన్న సమయాల్లో పూత రాకుండా ఉండే రీతిలో సరైనా&nbsp; సమయం చూసి విత్తే తేదిని నిర్ణయించుకోవాలి.</li>\r\n		<li>లవణ ద్రావణంలో విత్తనాల్ని నానబెడితే తెగులుని నివారించవచ్చు</li>\r\n		<li>తెగులు నిరోధక వంగడాలను (ఎస్.పి.వి. 191, సి.ఎస్.హెచ్ 5 వంటి రకాలు) వాడాలి.</li>\r\n	</ul>\r\n	</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n'),
(190, 'wcgh0n0beyxl17rri7xu', 'ojghf6cchb2g06ot2hnf+jt5c0q553u2cqn3yqhd7+', '<p><span style="font-size:20px"><strong>తెగులు</strong> <strong>నియంత్రణ</strong> <strong>&ndash;</strong> <strong>నివారణ &nbsp;పద్దతులు </strong></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li><span style="font-size:20px"><strong>రసాయన</strong> <strong>పద్దతులు</strong></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li>విత్తన శుద్ధి &ndash; 0.౩%&nbsp; థైరాంతో విత్తనాలను శుద్ధి చేయాలి.</li>\r\n	<li>కంకులు ఏర్పడే సమయంలో అప్పుడప్పుడు వానలు పడితే, వారమ తరువాత లేదా పాల దశలో, 0.25% మాన్కోజేబ్ లేదా 0.2% కాప్తాన్ లేదా లీటరు నీటికి 2 గ్రాముల కాప్తాన్, ఆరియో ఫంజి 200 పిపియం కలిపి చల్లాలి.&nbsp;&nbsp; &nbsp;&nbsp;</li>\r\n</ul>\r\n\r\n<p style="margin-left:108.0pt">&nbsp;</p>\r\n\r\n<ul>\r\n	<li><span style="font-size:20px"><strong>జీవ</strong> <strong>పద్దతులు</strong></span></li>\r\n	<li><strong><span style="font-size:13px; line-height:1.6em">సకాలంలో విత్తాలి. పరిస్థితులకు అనుగుణంగా విత్తే తేదిని నిర్ణయించుకోవాలి.</span></strong></li>\r\n	<li>తెగులు నిరోధక వంగడాలను (జి.ఎం.ఆర్.పి. 4., జి.ఎం.ఆర్.పి. 9, జి.ఎం.ఆర్.పి. 1౩, సి.ఎస్.వి 15 వంటి రకాలు) వాడాలి &nbsp;</li>\r\n</ul>\r\n\r\n<p style="margin-left:72.0pt">&nbsp;</p>\r\n'),
(191, 'xp58jjjoi7zs9jzbwys0', 'pgv6xcbyc3dexl2xjrq9+', '<p><span style="font-size:20px"><strong>తెగులు</strong> <strong>నియంత్రణ</strong> <strong>&ndash;</strong> <strong>నివారణ &nbsp;పద్దతులు </strong></span></p>\r\n\r\n<ul>\r\n	<li><span style="font-size:18px"><strong>రసాయన</strong> <strong>పద్దతులు</strong></span>\r\n\r\n	<ul style="list-style-type:circle">\r\n		<li>విత్తన శుద్ధి &ndash; కిలో విత్తనాలకు 4 గ్రాముల కాప్తాన్ లేదా థైరాం కలిపి విత్తన శుద్ధి చేయాలి</li>\r\n		<li>పంట 40 రోజుల దశలో,&nbsp; 0.25% మాన్కోజేబ్ చల్లాలి.&nbsp; తరువాత 15 రోజుల విరామంతో రెండు సార్లు చల్లాలి.</li>\r\n	</ul>\r\n	</li>\r\n</ul>\r\n\r\n<p style="margin-left:72.0pt">&nbsp;</p>\r\n\r\n<ul>\r\n	<li><span style="font-size:18px"><strong>జీవ</strong> <strong>పద్దతులు</strong></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li>తెగులు సోకని విత్తనాలను వాడాలి.</li>\r\n	<li>తెగులు సోకిన పంట అవశేషాలను ఏరి నాశనం చేయాలి.&nbsp;</li>\r\n</ul>\r\n'),
(192, 'xp58jjjoi7zs9jzbwys0', 'pgv6xcbyc3dexl2xjrq9+', '<p><span style="font-size:20px"><strong>తెగులు</strong> <strong>నియంత్రణ</strong> <strong>&ndash;</strong> <strong>నివారణ &nbsp;పద్దతులు </strong></span></p>\r\n\r\n<ul>\r\n	<li><span style="font-size:18px"><strong>రసాయన</strong> <strong>పద్దతులు</strong></span>\r\n\r\n	<ul style="list-style-type:circle">\r\n		<li>విత్తన శుద్ధి &ndash; కిలో విత్తనాలకు 4 గ్రాముల కాప్తాన్ లేదా థైరాం కలిపి విత్తన శుద్ధి చేయాలి</li>\r\n		<li>పంట 40 రోజుల దశలో,&nbsp; 0.25% మాన్కోజేబ్ చల్లాలి.&nbsp; తరువాత 15 రోజుల విరామంతో రెండు సార్లు చల్లాలి.</li>\r\n	</ul>\r\n	</li>\r\n</ul>\r\n\r\n<p style="margin-left:72.0pt">&nbsp;</p>\r\n\r\n<ul>\r\n	<li><span style="font-size:18px"><strong>జీవ</strong> <strong>పద్దతులు</strong></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li>తెగులు సోకని విత్తనాలను వాడాలి.</li>\r\n	<li>తెగులు సోకిన పంట అవశేషాలను ఏరి నాశనం చేయాలి.&nbsp;</li>\r\n</ul>\r\n'),
(193, 'm4lwmaxoykv1vlho0k5g', 'itb6y9h6o7xd1az1x4vt+jt5c0q553u2cqn3yqhd7+', '<p><span style="font-size:20px"><strong>తెగులు</strong> <strong>నియంత్రణ</strong> <strong>&ndash;</strong> <strong>నివారణ &nbsp;పద్దతులు </strong></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li><span style="font-size:20px"><strong>రసాయన</strong> <strong>పద్దతులు</strong></span></li>\r\n</ul>\r\n\r\n<p>విత్తన శుద్ధి&nbsp; - 0.5% సల్ఫర్ పౌడర్ లేదా 0.౩% థైరాం లేదా కాప్తాన్ తొ విత్తనాలను శుద్ధి చేయాలి.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li><span style="font-size:20px"><strong>జీవ</strong> <strong>పద్దతులు</strong></span></li>\r\n	<li><strong><span style="font-size:13px; line-height:1.6em">తెగులు లేని విత్తనాలు వాడాలి.</span></strong></li>\r\n	<li>పంటల మార్పిడి పాటించాలి</li>\r\n	<li>తెగులు సోకిన కంకులను బట్ట బ్యాగులలో సేకరించి, మరుగుతున్న వేడి నీళ్ళలో ముంచాలి</li>\r\n	<li>నంద్యాల, బిలిచిగాన్ వంటి తెగులు నిరోధక వంగడాలను వాడాలి.&nbsp;</li>\r\n</ul>\r\n\r\n<p style="margin-left:21.3pt">&nbsp;</p>\r\n\r\n<p style="margin-left:72.0pt">&nbsp;</p>\r\n'),
(194, 'pb5f6eartcs2r2ljlfxu', '', '<p><span style="font-size:20px"><strong>నివారణ చర్యలు :</strong></span></p>\r\n\r\n<p>రసాయన పద్దతులు&nbsp;</p>\r\n\r\n<p>ది మితోఎట్ &nbsp;2 మీ.లి &nbsp;లేదా మొనోక్రోటోఫోస్ &nbsp;1 .5 మీ. లి లీటరు నీటికి కలిపి పిచికారి చేయాలి&nbsp;&nbsp;</p>\r\n'),
(195, '30t6wfjajvcfmhuadojg', 'pgv6xcbyc3dexl2xjrq9+jt5c0q553u2cqn3yqhd7+', '<p><span style="font-size:20px"><strong>పురుగు నియంత్రణ</strong> <strong>&ndash;</strong> <strong>నివారణ &nbsp;పద్దతులు </strong></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li><span style="font-size:18px"><strong>రసాయన</strong> <strong>పద్దతులు</strong></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li>విత్తే సమయంలో, హెక్టారుకు 2.5 కిలోల ఫోరేట్ 10%,&nbsp; లేదా కార్బోఫురాన్ ౩% గుళికలను చల్లాలి.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</li>\r\n	<li>విత్తిన తరువాత, లేదా విత్తిన రెండో వారంలో,&nbsp; 0.07 % ఎండో సల్ఫాన్, లేదా 0.0005 % సైపర్ మేత్రిన్ లేదా 0.5 కిలోల కార్తాప్ హైడ్రో క్లోరైడ్ లేదా 0.5 కిలోల ట్రయజోఫాస్ , వారానికి రెండు సార్లు చల్లాలి.</li>\r\n	<li>0.2% కార్బారిల్ లేదా 0. 0౩% డై మీథోఎట్ చల్లాలి</li>\r\n	<li>కిలో విత్తనాలకు 10 గ్రాముల కార్బోఫురాన్ 50% ఎస్.పి. కలిపి విత్తన శుద్ధి చేయాలి. &nbsp;</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li><span style="font-size:18px"><strong>జీవ</strong> <strong>పద్దతులు</strong></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li>మామూలుగా హెక్టారుకు వాడే 10 కిలోల విత్తనానికి బదులు 1`2 కిలోల విత్తనాన్ని వాడాలి. విత్తిన నాలుగు వారాల తరువాత ఒత్తుగా ఉన్నచోట మొక్కలను పీకివేయాలి.&nbsp; ఈ పురుగు 4-5 వారాల వయస్సు ఉన్న మొక్కలను మాత్రమే ఆశిస్తుంది.</li>\r\n	<li>సకాలంలో విత్తాలి. ముందుగా విత్తితే పురుగు బాధ తక్కువగా ఉంటుంది. ఆలస్యంగా విత్తితే పురుగు తీవ్రత ఎక్కువగా ఉంటుంది.</li>\r\n	<li>పురుగు సోకిన మొక్కలను పీకిపారవేయాలి.</li>\r\n	<li>రోగ నిరోధక వంగడాలు వాదాలి.</li>\r\n</ul>\r\n\r\n<p style="margin-left:72.0pt">&nbsp;</p>\r\n'),
