INSERT INTO `machine` (`machine_id`, `icon`, `name`, `tname`, `category`, `powered`, `agri_id`, `other_crops`, `notes`, `address_id`, `subsidy_link`) VALUES
('1draqlv93plej2pnmbey', 'machines_paddy_drum_seeder1_poster.jpg', 'Paddy Drum Seeder', 'వరి డ్రం సీడర్ ', 'sowing', 'manual', 'd6w9z57rtt1hu5nm0svj+', '', '<p><span style="font-size:20px"><strong>వరి డ్రం సిడర్</strong></span></p>\r\n\r\n<p>అంతర్జాతియ వరి పరిశోధనాలయం వారు దీనిని రూపొందించారు</p>\r\n\r\n<p>దీనిని హైదరాబాద్ లోని వరి పరిశోధనాలయం వారు ఇక్కడి పరిస్తితులకు తగ్గట్లు మార్పులు చేసారు.</p>\r\n\r\n<p>ఇది తక్కువ బరువుతో ఎక్కడికైనా తీసుకోవచ్చు</p>\r\n\r\n<p>ఈ పరికరంలో రెండు చక్రాలు, నాలుగు డ్రములు ఇరుసు ద్వారా అమరి వుంటాయి.</p>\r\n\r\n<p>ప్రతి డ్రం 27 సెం.మీ పోడు వుంటుంది. 60సెంమీ చుట్టూ కొలతతో వుంటుంది.</p>\r\n\r\n<p>డ్రం పై భాగాన రెండు వరసలలో 7 మీ.మీ. వ్యాసం కలిగి రంధ్రాలు వుంటాయి.</p>\r\n\r\n<p>సాలుకు సాలుకు మధ్య 20సెం.మీ వుండేలా ఈ రంధ్రాలు వుంటాయి.</p>\r\n\r\n<p>డ్రం పై విత్తనాలు వేయడానికి, తీయడానికి మూత&nbsp; వుంటుంది.</p>\r\n\r\n<p>దీనిని ఒకరు లేదా ఇద్దరు తేలికగా లాగా గలుగుతారు.</p>\r\n\r\n<p>మడి చక్కగా చదునుగా దమ్ము చేసి వుండాలి</p>\r\n\r\n<p>24గంటలు నానబెట్టి, 24గంటలు మండి&nbsp; కట్టిన విత్తనాలను డ్రములో మూడు పాళ్ళు నింపాలి.</p>\r\n\r\n<p>విట్టుకునే ముందు నీరు పలుచని పారలగా వుండేలా చూడాలి.</p>\r\n\r\n<p>దీనితో 8 వరసలలో విత్తనం పడుతుంది</p>\r\n\r\n<p>విత్తిన 5 -7 రోజుల్లో ఎకరాకు 1 కిలో బుటాక్లోర్ కలుపు మందును చల్లాలి.</p>\r\n\r\n<p>ఒక మనిషి ఈ డ్రం సిడర్ ద్వారా రెండు ఎకరాల్లో విత్తుకోవచ్చు.</p>\r\n\r\n<p>ఆలస్యంగా నిరందే&nbsp; పొలాల్లో నేరుగా విత్తుకోవడానికి ఉపయోగకరంగా వుంటుంది.</p>\r\n\r\n<p>దీనివలన శ్రమ తగ్గుతుంది, కూలీల ఖర్చు తగ్గుతుంది, మ,ఒక్క సాంద్రత చక్కగా వుంటుంది.</p>\r\n\r\n<p>వరసల మధ్య కోనో విడర్ ద్వారా కలుపు తీయవచ్చు</p>\r\n\r\n<p>రెండు వరాల ముందే పంటకు కోతకు వస్తుందిదిగిబడిలో ఎలాంటి తేడా వుండదు</p>\r\n\r\n<p>దీనివలన ఒక తడి నీరు ఆదా అవుతుంది.</p>\r\n\r\n<p><span style="font-size:18px"><strong>వివరాలకు ;</strong></span></p>\r\n\r\n<p>వ్యవసాయ యంత్ర పరికరాల విభాగం</p>\r\n\r\n<p>ఆచార్య రంగా వ్యవసాయ విశ్వవిద్యాలయం, రాజేంద్ర నగర్ &ndash; 50 ౦౦ ౩౦</p>\r\n\r\n<p>లేదా</p>\r\n\r\n<p>వరి పరిశోధన సంచలనలయం, జగిత్యాల, కరీంనగర్ జిల్లా.</p>\r\n\r\n<p><span style="font-size:13px; line-height:1.6em">తయారుదారు విశ్వ కర్మ ఇంజనీరింగ్ కం.</span></p>\r\n', 'cey9w9z57rtt1hu5nm0s', ''),
('1fn7z77vlg0tndtoepyj', 'Machiines_groundnut_plant_digger_ashaguntaka_poster.png', 'Groundnut plant diggers', 'వేరుశనగ మొక్కలు పీకే పరికరాలు ', 'harvesting', 'motor', '', 'వేరుశనగ ', '<p><span style="font-size:18px"><strong>వేరు శనగ మొక్కలు పీకే&nbsp;పరికరాలు</strong></span></p>\r\n\r\n<p><span style="font-size:18px"><strong>(ఆశ గుంటక, బ్లెడ్&nbsp; గుంటక)</strong></span></p>\r\n\r\n<p>ఆషా&nbsp; గుంటకలో బ్లెడ్ వి ఆకారంలో వుంటుంది. బ్లెడ్ గుంట కలో తిన్నగా వుంటుంది.</p>\r\n\r\n<p>ఏవి రెండు 35 హెచ్ పి. ట్రాక్టర్తో పని చేస్తాయి.</p>\r\n\r\n<p>ఆశ గుంటక ఒకేసారి 6 వరసల్లోని వేరుశనగ మొక్కలను పికుతుంది</p>\r\n\r\n<p>బ్లెడ్ గుంటక ఒకేసారి 4 వరసల్లోని మొక్కలను పికుతుంది</p>\r\n\r\n<p>బ్లెడ్ గుంటక ఒకేసారి 4 వరసల్లో మొక్కలను పికుతుంది</p>\r\n\r\n<p>ఆశ గుంటక తో రోజుకు 10-12 ఎకరాల్లో వేరుశనగ మొక్కలు పికవచ్చు</p>\r\n\r\n<p>బ్లెడ్ గుంటకతో రోజుకు 7-10ఎకరాల్లో మొక్కలను పికవచ్చు</p>\r\n', 'rhy8yjpnpv8vwtgox53m', ''),
('4gnegoa4vfgmfbfpsc7t', 'machines_multi_crop_thresher_poster.png', 'Multi-Crop Thresher ', 'పలు పంటలు నూర్పిడి చేసే యంత్రం ', 'harvesting', 'motor', 'd6w9z57rtt1hu5nm0svj+woph9d7jl14v5cyhl7j1+qzzzf8al05d3ukawlk8y+21az1x4vtlq7981gemif+', '', '<p><span style="font-size:20px"><strong>పలు పంటల నూర్పిడి చేసే యంత్రం</strong></span></p>\r\n\r\n<p>దీనితో వరి, మొక్కజొన్న, జొన్న, సజ్జపంటలను త్వరగా తక్కువ ఖర్చుతో నుర్చవచ్చు.</p>\r\n\r\n<p>పంట నూర్చడమే కాకుండా విత్తనాలను జల్లెడ పట్టి మంచి గింజలను బయటకు పంపుతుంది.</p>\r\n\r\n<p>దీనిని నడుపుటకు నలుగురు మనుషులు అవసరం</p>\r\n\r\n<p>దీనిని ఉపయోగించి గంటకు 60౦ -10 ౦౦ కిలోల ధాన్యాన్ని నూర్చవచ్చు</p>\r\n\r\n<p>దీనిని నడుపుటకు 5 హెచ్ పి డీసెల్ ఇంజిన్ అవసరం.</p>\r\n', '27lnvqsagd869kxetdci', ''),
('4pi96rfn4tbozjx29nkk', 'machine_maize_sheller_electric_poster.png', 'Maize Shellers', 'మొక్కజొన్న వోలిచే యంత్రాలు ', 'harvesting', 'motor', 'qzzzf8al05d3ukawlk8y+', '', '<p><span style="font-size:18px"><strong>మొక్కజొన్న వోలిచే గొట్టం</strong></span></p>\r\n\r\n<p>గంటకు 10-15 కిలోల విత్తనాలు వలుస్తుంది</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style="font-size:18px"><strong>చేతిలో పనిచేసే పెద్ద సైజు షెల్లర్;</strong></span></p>\r\n\r\n<p>ఈ సదనం స్ప్రింగ్ ఆధారంగా పని చేస్తుంది. దీని బరువు సుమారు 6 కిలోలు. ఇది చేతిలో తిప్పడం వలన పని చేస్తుంది</p>\r\n\r\n<p>రోజుకు ౩౦౦-40౦ కిలోల గింజలను వేరు చేస్తుంది.</p>\r\n\r\n<p><span style="font-size:28px"><span style="font-size:18px"><strong style="font-size:13px; line-height:1.6em">విద్యుత్ మోటారుతో పనిచేసే షెల్లర్;</strong></span></span></p>\r\n\r\n<p>పల్ల డిస్క్ ను విద్యుత్ శక్తితో తిపుతుంది</p>\r\n\r\n<p>గంటకు సుమారు 35౦-40౦ కిలోల గింజలను వోలుస్తుంది</p>\r\n\r\n<p>దీనిలో ఒక్కొక్క కండెను మాత్రమే లోపలికి నెట్టాలి.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style="font-size:18px"><strong>విద్యుత్ పని చేసే మొక్కజొన్న వలిచే యంత్రం;</strong></span></p>\r\n\r\n<p>7-10 హెచ్ పి విద్యుత్ మోటార్ కావాలి</p>\r\n\r\n<p>డీసెల్, పెట్రోల్ మోటార్లను కూడా వాడవచ్చు.</p>\r\n\r\n<p>యంత్రంలోని సిలిండరు వేగం నిమిషానికి 40౦- 45౦ చుట్లు తిరుగుతుంది.</p>\r\n', 'rxyfo93w2z1ptsz81yb9', ''),
('4uteqzzzf8al05d3ukaw', 'machines_paddy_harvest_sickles_poster.png', 'Paddy Harvest Sickles', 'వరి కోత కోసే కొడవళ్ళు ', 'harvesting', 'manual', 'd6w9z57rtt1hu5nm0svj+', '', '<p><span style="font-size:20px"><strong>వరి కొత కోసే కొడవళ్ళు&nbsp;</strong></span></p>\r\n\r\n<p>నంది , వైభవ్&nbsp; కొడవళ్ళువీటిని నాణ్యమైన ఉక్కుతో తయారు చేస్తారుఅరుగుదల తక్కువగా వుంటుంది</p>\r\n\r\n<p>బరువు తక్కువగా వుండి తేలికగా వుంటాయి</p>\r\n\r\n<p>దేశవాళి కొడవళ్ళు కంటే 25-౩౦% ఎక్కువ పంట కోతకు&nbsp; తక్కువ శ్రమతో కోయవచ్చు</p>\r\n', 'lk8y9chly0wc1cupuerm', ''),
('58061vvch9obpgv6xcby', 'china_model.jpg', 'china model padddy transplanter', 'చైనా మోడల్ వరి నాటు యంత్రం ', 'sowing', 'motor', 'd6w9z57rtt1hu5nm0svj+', '', '<p>&nbsp;</p>\r\n\r\n<p style="margin-left:-4.5pt"><span style="font-size:20px"><strong>చైనా మోడల్ వరి నాటు యంత్రం</strong></span></p>\r\n\r\n<p style="margin-left:-4.5pt">ఒకేసారి 8 వరుసలలో నాట్లు&nbsp; వేస్తుంది</p>\r\n\r\n<p style="margin-left:-4.5pt">వరుసల మధ్య 23సెం.మీ. మొక్కల మధ్య 10-12 సెం.మీ ఎడం వుండేలా నారు నాటుకోవచ్చు</p>\r\n\r\n<p style="margin-left:-4.5pt">౩-5 హెచ్.పి. డిజిల్ ఇంజిన్ లో యంత్రం పని చేస్తుంది</p>\r\n\r\n<p style="margin-left:-4.5pt">ఒక్కో దుబ్బులో మొక్కల సంఖ్యను 2-7 వరకు మార్చుకునే వీలుంది</p>\r\n\r\n<p style="margin-left:-4.5pt">వరినారును ప్రత్యేకంగా పాలిథిన్ కవర్లపై పెంచాలి (మ్యాట్ విధానం)</p>\r\n\r\n<p style="margin-left:-4.5pt">వరినారు పెంచడానికి చెక్కతో చేసిన చట్రాలను DRR&nbsp;తయారు చేసింది.</p>\r\n\r\n<p style="margin-left:-4.5pt">చట్రాల సహయంతో&nbsp; బెడ్లను తయారు చేసుకొని మొలకెత్తిన వరి ధాన్యాన్ని మెల్లిగా అదమాలి. వరం రోజులకు లీటరు నీటికి ౩గ్రా. డిఎపి ఎరువును కలుపుకొని చల్లాలి.</p>\r\n\r\n<p>15-20 రోజుల నారు నాటడానికి&nbsp; వీలుగా వుంటుంది</p>\r\n\r\n<p>రోజుకు రెండు ఎకరాల నాట్లు వేయవచ్చు</p>\r\n', 'c3dexl2xjrq9q8q30t6w', ''),
('58fn7z77vlg0tndtoepy', 'machines_rayala_gorru1_poster.png', 'Rayala Gorru (Seed cum Fertilizer)', 'రాయల గొర్రు ', 'sowing', 'bullock', 'xxsll6xdybjjrcjc3p5i+qzzzf8al05d3ukawlk8y+', 'వేరుశనగ ', '<p><span style="font-size:20px"><strong>రాయల గొర్రు</strong></span></p>\r\n\r\n<p>ఎద్దులతో లాగడానికి తయారు చేయబడింది</p>\r\n\r\n<p>ఇది పూర్తిగా ఇనుముతో చేయబడిన పరికరాలుదీనికి ఇరువైపులా దిండు మీద రెండు జేద్ది గాలు అమరి వుంటాయి. వీటి ద్వారా విత్తనం, ఎరువులు సరైన లోతులో వేసుకునే వీలుంది.</p>\r\n\r\n<p>రోజుకు ౩-5 ఎకరాలలో రాయల గొర్రు ద్వారా విత్తుకోవచ్చు</p>\r\n\r\n<p>కంది, మొక్కజొన్న, వేరుశనగ పంటలను విత్తడానికి అనువుగా వుంటుంది</p>\r\n\r\n<p>&nbsp;</p>\r\n', 'jrhy8yjpnpv8vwtgox53', ''),
('76m3qmqoulejghkdc9na', 'machines_cridss_cultivators.png', 'CRIDAs Cultivator', 'క్రీడా వారి గొర్రు నాగలి ', 'weeding', 'bullock', 'woph9d7jl14v5cyhl7j1+', 'అపరాలు ', '<p><span style="font-size:18px"><strong>గొర్రు నాగలి (క్రిడా వారి)</strong></span></p>\r\n\r\n<p>ఎడ్లతో లాగబడే పరికరం</p>\r\n\r\n<p>దీనితో అపరాలు, జొన్న విత్తనాలు విత్తుకోవచ్చు</p>\r\n\r\n<p>రోజుకు రెండు ఎకరాలలో పని పూర్తి అవుతుంది</p>\r\n\r\n<p>నాగలికి ఎరువు విత్తనం పెట్టెలు అమర్చబడి వుంటాయి</p>\r\n', 'khso82nopsgz7uy0fnfi', ''),
('adet1j7gdrdb5dq992l6', 'machines_MB_plough_poster.png', 'M.B Plough', 'రెక్క నాగలి ', 'tillage', 'motor', 'd6w9z57rtt1hu5nm0svj+5msr3s5clh36mc9s6hev+woph9d7jl14v5cyhl7j1+xxsll6xdybjjrcjc3p5i+qzzzf8al05d3ukawlk8y+', '', '<p><span style="font-size:20px"><strong>రెక్క నాగలి</strong></span></p>\r\n\r\n<p>దీనిని ఇనుప నాగలి అని పిలుస్తారు.</p>\r\n\r\n<p>ఇది దుక్కి దున్నడానికి ఉపయోగించే పరికరం.</p>\r\n\r\n<p>దీనివలన కలుపు మొక్కలు కూడా నివారిమ్చవచ్చు</p>\r\n\r\n<p>పెద్ద పెద్ద మట్టి గడ్డలు పగిలిపోతాయి, నేలలో వున్న కీటకాలు బయటికి వస్తాయి.</p>\r\n\r\n<p>దున్నిన మొదటిసారిగా నేల చక్కగా చదును చేయబడతాయి.</p>\r\n\r\n<p>వరి చేలలో, పొడి దుక్కికి లేదా మెట్ట దుక్కికి 6 అంగుళాల రెక్క నాగలి అవసరం</p>\r\n\r\n<p>దీని బరువు సుమారు 8 కిలోల వుంటుంది</p>\r\n\r\n<p>జత ఎద్దులతో లేదా ట్రాక్టర్ తో దీనిని వాడవచ్చు</p>\r\n\r\n<p>దీనితో ౩౦సెం.మీ. లోతు దుక్కి దున్నవచ్చు.</p>\r\n\r\n<p>రెండు సార్లు ఇనుప నాగలికి 45 హెచ్ పి ట్రాక్టర్ వాడాలి</p>\r\n\r\n<p>ఎద్దులతో లాగబడే నాగలితో రోజుకు 1 &frac12; ఎకరం భావించి దుక్కి చేయవచ్చు.</p>\r\n\r\n<p>ట్రాక్టర్ తో లాగబడే నాగలితో రోజుకు 4 ఎకరాలు దున్నవచ్చు</p>\r\n\r\n<p>ఎడ్లు లాగే ఇనుప నాగలి సైజు 40 సెం.మీ. వుంటుంది.</p>\r\n\r\n<p>&nbsp;</p>\r\n', 'xgihl6vd19y6t3pvnidj', ''),
('beycey9w9z57rtt1hu5n', 'machines_disc_plough5_poster.jpg', 'Disc Plough', 'పల్లెపు నాగలి ', 'tillage', 'motor', '5msr3s5clh36mc9s6hev+woph9d7jl14v5cyhl7j1+xxsll6xdybjjrcjc3p5i+qzzzf8al05d3ukawlk8y+21az1x4vtlq7981gemif+wm1mxo3dpi9gzuol4mld+69n6t0xdfosjrem0am5a+', '', '<p style="margin-left:-4.5pt"><span style="font-size:18px"><strong>పల్లెపు నాగలి&nbsp;</strong></span></p>\r\n\r\n<p style="margin-left:-4.5pt">ట్రాక్టర్తో లాగాబడుతుంది</p>\r\n\r\n<p style="margin-left:-4.5pt">రోజుకు 21/2 నుండి &nbsp;3&nbsp;ఎకరాలకు దుక్కి దున్నవచ్చు</p>\r\n\r\n<p style="margin-left:-4.5pt">తేమ తక్కువగా వుండి, గట్టిగా వున్న నేలలకు ఇది చాల అనుకూలం.</p>\r\n\r\n<p style="margin-left:-4.5pt">గత పంట అవశేషాలున్న భూములను కూడా దుక్కి చేయగలదు</p>\r\n\r\n<p style="margin-left:-4.5pt">పళ్ళెం&nbsp; వ్యాసం 50-70 సెం.మీ వ్యాసం వుంటుంది. దీని బరువు 18౦-60౦ కిలోల వరకు వుంటుంది. దీనికి రాయితీ లభిస్తుంది.</p>\r\n', 'm0svji82zs0px8mm1gsb', ''),
('cgh0n0beyxl17rri7xun', 'machines_two_row_seeder1_poster.png', 'ఎద్దులతో లాగబడే రెండు సాల్ల విత్తన పరికరం ', 'Bullock drawn two row seeder', 'sowing', 'bullock', '', '', '<p style="margin-left:-4.5pt"><span style="font-size:20px"><strong>ఎద్దులతో లాగబడే రెండు సాల్ల విత్తన పరికరం</strong></span></p>\r\n\r\n<p style="margin-left:-4.5pt">దీనితో ఒకేసారి విత్తనం, ఎరువు వేయవచ్చు</p>\r\n\r\n<p style="margin-left:-4.5pt">ఈ పరికరంతో సాల్ల మధ్య దురాన్ని, విత్తనం పడే లోతును&nbsp; మార్చుకునే వీలుంది.</p>\r\n\r\n<p style="margin-left:-4.5pt">దీని బరువు నిర్వహణ ఖర్చు తక్కువదేనితో రోజుకు 4-5 ఎకరాల్లో విత్తనం వేయవచ్చు.</p>\r\n\r\n<p style="margin-left:-4.5pt">జత ఎద్దులు సరిపోతాయి.</p>\r\n\r\n<p style="margin-left:-4.5pt">&nbsp;</p>\r\n', 'upd5182xxsll6xdybjjr', ''),
('cjc3p5idbdw49dinmeq3', 'machines_handdriven_castor_thresher3_poster.png', 'Hand driven castor thresher', 'చేతితో తిప్పే అముదపు కాయలు వలిచే యంత్రం ', 'harvesting', 'manual', '', 'ఆముదం ', '<p style="margin-left:-4.5pt"><span style="font-size:20px"><strong>చేతితో పనిచేసే ఆముదపు కాయలు ఒలిచే యంత్రం</strong></span></p>\r\n\r\n<p style="margin-left:-4.5pt">చేతితో తిప్పడం ద్వారా గేర్ వ్యవస్థ వలన సిలిండరు తిరుగుతుంది</p>\r\n\r\n<p style="margin-left:-4.5pt">ఆముదం కాయలను హపెర్ లో వేయాలి.</p>\r\n\r\n<p style="margin-left:-4.5pt">కాయలు పగిలిన తరువాత తొక్కలు ఒక ద్వార బయటకు వస్తాయి</p>\r\n\r\n<p style="margin-left:-4.5pt">చేతిలో చెరగడం ద్వారా వేరు చేయాలి</p>\r\n\r\n<p style="margin-left:-4.5pt">గింజ నష్టం 1% మాత్రమే</p>\r\n\r\n<p style="margin-left:-4.5pt">గింజల మొలక శాతం 80% వుంటుంది</p>\r\n\r\n<p style="margin-left:-4.5pt">మార్పిడి సామర్ధ్యం 80%</p>\r\n\r\n<p style="margin-left:-4.5pt">గంటకు 15౦ కిలోల కాయలు నుర్చవచ్చు</p>\r\n', 'ambzitwk4jmt5lbp8ne0', ''),
('d47jpd159xsr8y47w1ov', 'machines_tropiculter_poster_(3).png', 'Tropiculter', 'ట్రాపికల్తర్  ', 'other', 'bullock', 'woph9d7jl14v5cyhl7j1+xxsll6xdybjjrcjc3p5i+qzzzf8al05d3ukawlk8y+21az1x4vtlq7981gemif+', '', '<p style="margin-left:-4.5pt"><span style="font-size:20px"><strong>ట్రాపి కల్టర్ </strong></span></p>\r\n\r\n<p style="margin-left:-4.5pt">ఇది ఒక బహోలార్ధక సాధక పనిముట్టు</p>\r\n\r\n<p style="margin-left:-4.5pt">దీనిని ఇంక్రిసాట్ రూపొందించింది</p>\r\n\r\n<p style="margin-left:-4.5pt">దీనికి ఇతర పనిముట్లు బిగించుకొని వివిధ పనులు ఏకకాలంలో చేయవచ్చు.</p>\r\n\r\n<p style="margin-left:-4.5pt">సాగు చేయడం, విత్తడం, ఎరువు వేయటం, అంతర కృషి, బోదెల సాల్లు&nbsp; చేయడం మొదలైన పనులు ట్రాపి కల్చర్ ద్వారా చేయవచ్చు.</p>\r\n\r\n<p style="margin-left:-4.5pt">ఎద్దులతో ఈ పరికరం లగాబడుతుంది</p>\r\n\r\n<p style="margin-left:-4.5pt">ఒక రోజులో 2 హెక్టార్ల పని పూర్తి చేస్తుంది.</p>\r\n\r\n<p style="margin-left:-4.5pt">సాంప్రదాయ పద్దతులలో పోలిస్తే దీనిద్వారా ఖర్చు తక్కువసాల్ల మధ్య దూరం, విత్తనా వేసే లోతు మనం సరి చూసుకోవచ్చు.</p>\r\n\r\n<p style="margin-left:-4.5pt">ఎద్దులు తోలే వ్యక్తి కాకుండా వెనక వైపు ఇంకొక వ్యక్తి అవసరం.</p>\r\n\r\n<p style="margin-left:-4.5pt">దీనితో రోజుకు రెండు హెక్టార్లు అంతర కృషి చేయవచ్చు.</p>\r\n\r\n<p style="margin-left:-4.5pt">50% రాయితో అగ్రోస్ వారు అందిస్తుంది.</p>\r\n\r\n<p style="margin-left:-4.5pt">దీనిలో 1.5 టన్నుల సరుకులు రవణా చేసుకోవచ్చు.</p>\r\n\r\n<p style="margin-left:-4.5pt">తక్కువ శ్రమతో ఎక్కువ పని చేయవచ్చు.</p>\r\n\r\n<p style="margin-left:-4.5pt">దీని నిర్వహణ ఖర్చు ట్రాక్టర్ కంటే చాలా తక్కువ.</p>\r\n\r\n<p style="margin-left:-4.5pt">మేకిన్స్ ఆగ్రో ప్రొడక్ట్స్ లిమిటెడ్, 6-౩-1090/బి/2, రాజ భవన్ రోడ్, సోమాజిగూడ, హైదరాబాద్ &ndash; 82.</p>\r\n', 'q45fyt7ibktuuwyimzax', ''),
('d76m3qmqoulejghkdc9n', 'machines_tractor_operated_seed_cum_ferti_gorru1_poster.png', 'Tractor operated seed cum ferti gorru', 'ట్రాక్టర్ తో నడిచే విత్తనం ఎరువు ఒకేసారి వేసే గొర్రు ', 'sowing', 'motor', 'qzzzf8al05d3ukawlk8y+', 'వేరుశనగ , ఆముదం ', '<p><span style="font-size:20px"><strong>ట్రాక్టర్ తో నడిచే విత్తనం </strong><strong>&ndash;</strong> <strong>ఎరువు ఒకేసారి వేసే గొర్రు</strong></span></p>\r\n\r\n<p>దీనిని ట్రాక్టర్ 9 చెక్కిల కల్టివేటర్ కు బిగుం చుకొని వాడాలి.</p>\r\n\r\n<p>దీనిని ఉపయోగించి వేరుశనగ, ఆముదాలు, మొక్కజొన్న మొదలగున్నవి&nbsp; గింజ సైజు అధికంగా వుండే విత్తనాలను విత్తుకోవచ్చు</p>\r\n\r\n<p>ఈ పరికరం వాడి రోజుకు 7 -8 ఎకరాలలో పంటను విత్తుకోవచ్చు</p>\r\n\r\n<p>పై మనుషుల అవసరం వుండదు</p>\r\n\r\n<p>ఇది 35 హెచ్ పి ట్రాక్టర్ తో నడపబడుతుంది.</p>\r\n\r\n<p>విత్తనం డబ్బా నుండి విత్తనాలు ట్యూబ్ ద్వార నేలలోకి పడునట్లు ఏర్పాటు వుండి</p>\r\n\r\n<p>విత్తనానికి వితనానికి 10 సెం.మీ. దూరంలో విత్తు తుంది. ౩-5 సెం.మీ. లోతులో విత్తనం పడుతుంది.</p>\r\n\r\n<p>క్రీడా వారు తయారు చేసిన పరికరంలో విత్తనం, ఎరువుల కొరకు వేరు వేరు డబ్బాలు వుంటాయి.</p>\r\n', 'akhso82nopsgz7uy0fnf', ''),
('erm4gnegoa4vfgmfbfps', 'machines_poweroperated_paddy_harvester.jpg', 'Power Operated Paddy Harvester', 'ఇంజన్ తో నడిచే వరికోత యంత్రం ', 'harvesting', 'motor', 'd6w9z57rtt1hu5nm0svj+', '', '<p style="margin-left:-4.5pt"><span style="font-size:18px"><strong>ఇంజిన్ తో నడిచే వరి కోత యంత్రం</strong></span></p>\r\n\r\n<p style="margin-left:-4.5pt">ఇది 5 హెచ్ .పి. డీజిల్ ఇంజిన్ లో పని చేస్తుంది.</p>\r\n\r\n<p style="margin-left:-4.5pt">ఒక మనిషి సులువుగా నడపగలిగెంతగా ఈ యంత్రం వుంటుంది. గంటన్నర కాలంలో 1 ఎకరం కోత కోయవచ్చు.</p>\r\n\r\n<p style="margin-left:-4.5pt">దీనితో రోజుకు 4 ఎకరాలలో వరి పంట కోయవచ్చు</p>\r\n\r\n<p style="margin-left:-4.5pt">కుసుమ పంటను కూడా కోయడానికి ఉపయోగించవచ్చు</p>\r\n\r\n<p style="margin-left:-4.5pt">దీని ఖరీదు రూ. 65,౦౦౦</p>\r\n\r\n<p style="margin-left:-4.5pt">పంట నష్టం చాల తక్కువుగా వుంటుంది.</p>\r\n\r\n<p style="margin-left:-4.5pt">ఉపయోగించే ముందు పొలంలో ఎక్కువ తేమ లేకుండా చూడాలి.</p>\r\n\r\n<p style="margin-left:-4.5pt">యంత్రం నడిపే ముందు గట్ల వెంట చుట్టూ అర్ధ గజం మేర కడవాల్లతో కోత కోయాలి</p>\r\n\r\n<p style="margin-left:-4.5pt">యంత్రంలో వృతాకరంలో తిప్పుతో కోత కోయాలి.</p>\r\n\r\n<p style="margin-left:-4.5pt">గంటకు 1 లీటరు డిజిల్ ఖర్చు అవుతుంది.</p>\r\n\r\n<p style="margin-left:-4.5pt">దీని బరువు సుమారు 165 కిలోలు</p>\r\n\r\n<p style="margin-left:-4.5pt">కట్టకు బారు వెడల్పు 125 సెం.మీ.</p>\r\n', 'c7t27lnvqsagd869kxet', ''),
('fhioq822ebnx98bod1m1', 'machines_pedling_paddy_harvester1_poster.png', 'Peddling paddy harvester ', 'కాళ్ళతో పనిచేసే వరి నూర్పిడి యంత్రం ', 'harvesting', 'manual', 'd6w9z57rtt1hu5nm0svj+', '', '<p><span style="font-size:20px"><strong>కాళ్ళ తో &nbsp;పనిచేసే వరి నూర్పిడి యంత్రం</strong></span></p>\r\n\r\n<p>ఇరుసుకి ఒక డ్రం బిగించబడి వుంటుంది. దీనిపై యు ఆకారంలో ఇనుప తీగల అమర్చబడి వుంటుంది.</p>\r\n\r\n<p>కాలిలో తొక్కినప్పుడు ఈ డ్రం గుండ్రంగా తిరుగుతుంది</p>\r\n\r\n<p>గింజలలో వున్న వరి పనలను వేగంగా తిరిగే డ్రం కు దగ్గరగా తీసుకువచ్చినప్పుడు ఇనుప తీగల గింజలను రాల్చుతాయి.</p>\r\n\r\n<p>దీని ద్వారా రోజుకు ౩ &frac12; నుండి 4 క్వింటాళ్ల ధాన్యాన్ని నుర్చవచ్చు</p>\r\n', 'nmpgsvah5207yg3i3jbz', ''),
('fp086arl19mf0zoorcyo', 'machines_groundnut_pod_stripper_mobile_poster.png', 'Groundnut pod stripper(Mobile machine)', 'వేరుశనగ కాయలు విడదీయడానికి పెద్ద కదిలే యంత్రం ', 'harvesting', 'motor', '', 'వేరుశనగ ', '<p><span style="font-size:18px"><strong>వేరుశనగ కాయలు విడదీయడానికి కదిలే పెద్ద యంత్రం</strong></span></p>\r\n\r\n<p>దీనిని ఆచార్య రంగా విశ్వవిద్యాలయం వారు రూపొందించారు</p>\r\n\r\n<p>పీకిన మొక్కలను దీనిలో వేస్తే వేరు చేయబడిన కాయలు ఒక పక్క, మిగిలిన మొక్కలు వెనక బాగా వస్తాయి.</p>\r\n\r\n<p>ఇది 5 హెచ్ పి డీజిల్ ఇంజిన్ లో పనిచేస్తుంది.</p>\r\n\r\n<p>యంత్రానికి టైరులు వుండటం వలన ఇతర ప్రదేశాలకు తిసుకుపోగలం</p>\r\n\r\n<p>ఈ యంత్రం కొరకు 5గురు కూలీల అవసరం వుంది</p>\r\n\r\n<p>2-౩ రోజులు పొలంలో ఎండిన మొక్కలను యంత్రంలో వుండి</p>\r\n\r\n<p>ఈ యంత్రంలో కాయలు వేరు చేయబడటమే కాకుండా శుబ్రపరచబడతాయి</p>\r\n\r\n<p>ఈ యంత్రం ద్వారా గంటకు 200`౩౦౦ కిలోల కాయలు వేరుచేయబడతాయి.</p>\r\n\r\n<p>ఈ యంత్రం వలన ౩౦% డబ్బు , 70% సమయం ఆదా అవుతుంది</p>\r\n', 'yo42bbz5x30h47jpd159', ''),
('gvnzoqzo24qijf5nmpax', 'Groundnut_cum_castor_thresher_poster.png', 'Groundnut cum castor thresher', 'వేరుశనగ -ఆముదం కాయలు వలిచే యంత్రం ', 'harvesting', 'motor', '', 'వేరుశనగ , ఆముదం ', '<p><span style="font-size:18px"><strong>వేరుశనగ &ndash;ఆముదం కాయలు వలిచే యంత్రం</strong></span></p>\r\n\r\n<p>వేరుశనగ కాయలను మొక్కలను వేరుచేసే యంత్రాన్ని కొద్దిపాటి మార్పులతో వేరుశనగ, ఆముదం కాయలు వలిచే యంత్రంగా మార్చవచ్చు</p>\r\n\r\n<p>జల్లెడలు, కంకేవ్, సిలిండర మార్చడం ద్వారా ఇది పని చేస్తుంది.</p>\r\n\r\n<p>గింజల నష్టం ౩ శాతం లోపు వుంటుంది.</p>\r\n\r\n<p>రోజుకు ౩-4 టన్నుల ఆముదం లేదా వేరుశనగ కాయలను వలవ వచ్చు</p>\r\n\r\n<p>దీనివలన కూలిలో ఆదా అవుతుంది. నష్టం తగ్గుతుంది.</p>\r\n\r\n<p>ఇది 8-10 హెచ్.పి. డీజిల్ ఇంజిన్ తో పని చేస్తుంది.</p>\r\n', 'no7il65gcqkmy9tkh36e', ''),
('iclh36mc9s6hev8nlq4n', 'mchines_seed_cum_ferti_drill_poster.png', 'Seed cum Ferti Drill ', 'శనగ , జొన్న విత్తనాలు ఎరువులు ఒకేసారి వేసే పరికరం ', 'sowing', 'motor', 'woph9d7jl14v5cyhl7j1+', 'శనగ ', '<p><span style="font-size:20px"><strong>విత్తనం </strong><strong>` </strong><strong>ఎరువు ఒకేసారి వేసే పరికరం</strong></span></p>\r\n\r\n<p>దీని ద్వారా శనగ, జొన్న పంటల విత్తనాలను, ఎరువును ఒకేసారి వేయవచ్చు.</p>\r\n\r\n<p>నంద్యాల ప్రాంతీయ వ్యవసాయ పరిశోధక కేంద్రం వారు దీనిని రూపొందించారు.</p>\r\n\r\n<p>దీనితో ఒకేసారి 8 సాల్లలో విట్టుకొనే వీలుంది</p>\r\n\r\n<p>ఈ పరికరంలో విత్తన మోతాదును నియంత్రించే వీలుంది</p>\r\n\r\n<p>దీనిలో 8 సల్ల గొర్రు, విత్తనపు అర, విత్తనా కంట్రోల్ రబ్బర్లు, షాప్ట్ , చైను పల్ల చక్రాలు, గ్రౌండ్ వాలు, అల్యూమినియం గంటలు, ప్లాస్టిక్ గాట్టలు వుంటాయి.</p>\r\n\r\n<p>ఈ పరికరం ద్వారా ఎకరానికి 10 -20 కిలోల శనగ విత్తనం ఆదా అవుతుంది</p>\r\n\r\n<p>ఎరువు పడే మీటరింగ్ &nbsp;రంధ్రాన్ని మనకు కావలసిన రీతిలో మార్చుకోవచ్చు</p>\r\n\r\n<p>దీనిద్వారా రోజుకు 15-20 ఎకరాలలో విత్తడం పూర్తి చేసుకోవచ్చువ్యవసాయ శాఖ వారు సబ్సిడీ లబిస్తుంది.</p>\r\n\r\n<p>మహబూబ్ ఆటో ఏజెన్సీస్, డోర్ నం.25/456 ఎ, రైల్వే స్టేషన్ రోడ్, నంద్యాల &ndash; 518 50౩, ఫోన్; 9849751998.</p>\r\n\r\n<p>లేదా</p>\r\n\r\n<p>అసోసియేట్ డైరెక్టర్ ఆఫ్ రీసెర్చ్, ప్రాంతీయ వ్యవసాయ పరిశోధన స్తానం, నంద్యాల &ndash; 5185౦1, ఫోన్; 08514-242296.</p>\r\n', 'wla0oov3k2xnnt1yetrm', ''),
('j4raqoqley8ci4l6ir52', 'machines_korea_model_paddy_transplanter.png', 'Korea Model Paddy Transplanter', 'కొరియా మోడల్ వారి నటు యంత్రం ', 'sowing', 'motor', 'd6w9z57rtt1hu5nm0svj+', '', '<p><span style="font-size:18px"><strong>కొరియా మోడల్ వరినాటు&nbsp;యంత్రం;</strong></span></p>\r\n\r\n<p>ఈ యంత్రం సహయంతో ఒకేసారి 4 వరసల్లో నాట్లు వేసుకోవచ్చు</p>\r\n\r\n<p>వరసల మధ్య ౩౦ సెం.మీ. మొక్క మధ్య 10-12 సెం.మీ.దూరంలో నారును నాటుకోవచ్చు.</p>\r\n\r\n<p>ఈ యంత్రం ౩.5 హెచ్.పి. పెట్రోల్ ఇంజిన్తో పని చేస్తుంది.</p>\r\n\r\n<p>నారును ప్రత్యేకంగా పాలిథిన్ కవర్ మీద పెంచాలి.</p>\r\n\r\n<p>చెక్క ఫ్రేమ్ లను ఉపయోగించి నారు మడులను తయారు చేసి, ఒక్కో అరలో సుమారు 13౦&nbsp; గ్రా. మొలకెత్తిన వరి విత్తనాలను చల్లాలి.</p>\r\n\r\n<p>15-20 రోజులలో నాటడానికి నారు సిద్దంగా వుంటుంది</p>\r\n\r\n<p>రోజుకు ౩ ఎకరాల్లో వరి నాట్లు వేయవచ్చు</p>\r\n', '2d9n8txqafffbt9z194s', ''),
('jtdci6eh2rtu1qpmmi8z', 'machines_self_propelled_paddy_row_seeder4_poster.png', 'Self Propelled Paddy Row Seeder', 'స్వశక్తి తో నడిచే వరి విత్తన యంత్రం.', 'sowing', 'motor', 'd6w9z57rtt1hu5nm0svj+', '', '<p style="margin-left:-4.5pt"><span style="font-size:20px"><strong>స్వశక్తితో నడిచే వరి విత్తన యంత్రం</strong></span></p>\r\n\r\n<p style="margin-left:-4.5pt">10 వరుసలలో విత్తుతుంది.</p>\r\n\r\n<p style="margin-left:-4.5pt">5 హెచ్ పి సామర్ధ్యం కలిగిన డిజిల్ ఇంజిన్ సహాయంతో పని చేస్తుంది.</p>\r\n\r\n<p style="margin-left:-4.5pt">సాల్ల మధ్య 20 సెం.మీ. మొక్కల మధ్య 10 సెం.మీ. ఎడం వుండునట్లు విత్తడం జరుగుతుంది.</p>\r\n\r\n<p style="margin-left:-4.5pt">హెక్టారుకు 7 కిలోల విత్తనాలు సరిపోతాయి.</p>\r\n\r\n<p style="margin-left:-4.5pt">ఒకేచోట ౩-4 విత్తనాలు పడుతాయి.</p>\r\n\r\n<p style="margin-left:-4.5pt">గంటకు లీటరు డీజిల్ ఖర్చు అవుతుంది.</p>\r\n\r\n<p style="margin-left:-4.5pt">యంత్ర వేగం గంటకు 2-4 కి.మీ.</p>\r\n\r\n<p style="margin-left:-4.5pt"><span style="font-size:13px; line-height:1.6em">దీనిని ఆగ్రో ఇండస్ట్రీస్, విశ్వ కర్మ , కర్షక వారు తయారు చేస్తున్నారు.</span></p>\r\n\r\n<p style="margin-left:-4.5pt">వ్యవసాయ శాఖ రాయితీ కల్పిస్తుంది.</p>\r\n', 'dfpe7idkub2mjfy6jajo', ''),
('khey8ci4l6ir522d9n8t', 'machine_cultivators_poster.png', 'Cultivator', 'గొర్రు ', 'weeding', 'motor', '5msr3s5clh36mc9s6hev+woph9d7jl14v5cyhl7j1+qzzzf8al05d3ukawlk8y+21az1x4vtlq7981gemif+wm1mxo3dpi9gzuol4mld+69n6t0xdfosjrem0am5a+', '', '<p style="margin-left:-4.5pt"><span style="font-size:18px"><strong>గొర్రు</strong></span></p>\r\n\r\n<p style="margin-left:-4.5pt">రెక్క నాగలి పల్లపు నాగలి మాదిరిగానే వాడవచ్చు- దీనిని మెత్త దుక్కి చేయడానికి ఉపయోగిస్తారు.</p>\r\n\r\n<p style="margin-left:-4.5pt">దీనితో అంతర కృషి కూడా చేయవచ్చు.</p>\r\n\r\n<p style="margin-left:-4.5pt">కొన్ని పరికరాలు కూడా చేయవచ్చుకొన్ని పరికరాలు అమర్చి విత్తనం విత్తుకోవచ్చు</p>\r\n\r\n<p style="margin-left:-4.5pt">విత్తనం, ఎరువుల ఒకేసారి వేసే గోర్రులు కూడా అందుబాటులోకి వచ్చాయి.</p>\r\n\r\n<p style="margin-left:-4.5pt">గొర్రుల ఇనుప చట్రానికి కర్రులు బిగించబడి వుంటాయి. ఇవి స్ప్రింగ్ లతో &nbsp;వుండేవి, స్థిరంగా వుండేవి రెండు రకాలుగా వుంటాయి.</p>\r\n\r\n<p style="margin-left:-4.5pt">వాటిని ట్రాక్టర్ తో పాటు ఎడ్లతో లాగి కూడా పనిచేయించ వచ్చు</p>\r\n', 'xqafffbt9z194scczhi7', ''),
('kn1gzuol4mldqhlrg1sa', 'machines_groundnut_pod_stripper_poster.jpg', 'Groundnut pod stripper ', 'వేరుశనగ కాయలు విడదీసే యంత్రం ', 'harvesting', 'motor', '', 'వేరుశనగ ', '<p><span style="font-size:18px"><strong>వేరుశనగ కాయలను విడదీసే యంత్రం: </strong></span></p>\r\n\r\n<p><strong>ఈ&nbsp; యంత్రం 2 హెచ్.పి విద్యుత్ మోటార్ సహాయంతో నడుస్తుంది. </strong></p>\r\n\r\n<p><strong>పొలం నుంచి తీసిన పంటను ఎండబెట్టనవసరం లేకుండా వెంటనే ఈ యంత్రం సహాయంతో కాయలను వేరుచేయవచ్చు. </strong></p>\r\n\r\n<p><strong>ఈ యంత్రం నడిచేటప్పుడు వేరుశనగ మొక్కలను అందించటానికి, యంత్రంలో ఉంచటానికి, విడదీసిన వేరుశనగ కాయలను పోగు చెయ్యటానికి ఐదుగురు కూలిలూ కావాలి. </strong></p>\r\n\r\n<p><strong>ఈ యంత్రం కాయలను పూర్హిగా విడదీస్తుంది, కాయ పగలదు. కాయలను విడదియగా మిగిలిన మొక్కలు&nbsp; ఏ మాత్రం పాడు కావు గనుక పశువుల దాణాగా &nbsp;ఉపయోగించుకోవచ్చు.&nbsp; </strong></p>\r\n\r\n<p><strong>ఈ యంత్రం నందు పనిచేసే కూలీల చేతులు యంత్రానికి తగలకుండా జాగ్రత్త పడాలి,తాము కట్టుకున్న బట్టలు యంత్రంలో తిరిగే భాగాలకు తగలకుండా జాగ్రత్త పడాలి</strong></p>\r\n\r\n<p><span style="font-size:13px; line-height:1.6em">దీనితో గంటకు 100 కిలోల కాయలను మొక్కల నుండి వేరు చేయవచ్చుయంత్రం బరువు సుమారు 115 కిలోలు</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', 'dcaoodurhkcl6znouq0w', ''),
('ktuuwyimzaxgwi5ohu70', 'machines_paddy_winnower_motor1_poster.png', 'Paddy winnower (electric)', 'వడ్లు తూర్పార పట్టే గాలి పంఖా (మోటార్) ', 'harvesting', 'motor', 'd6w9z57rtt1hu5nm0svj+', '', '<p><span style="font-size:20px"><strong>తూర్పార బట్టి గాలి పంఖా</strong></span></p>\r\n\r\n<p>వరి దాన్యాన్ని తూర్పార పట్ట డానికి ఉపయోగం</p>\r\n\r\n<p>దానిలో ౩ బ్లేడ్ల పంఖా వుంటుంది.</p>\r\n\r\n<p>జాగ్రత్త కొరకు జాలి వుంటుంది</p>\r\n\r\n<p>ఇద్దరు మనుషులు తూర్పార పట్టడానికి అవసరం</p>\r\n\r\n<p>కరెంటు మోటార్ తో పంఖా నడుస్తుంది</p>\r\n', 'nsygf705spkif0cug3qb', ''),
('mpgsvah5207yg3i3jbzz', 'machines_crida_planter_poster.png', 'CRIDA Planter', 'క్రీడా ప్లాంటర్', 'tillage', 'motor', 'qzzzf8al05d3ukawlk8y+', 'వేరు శనగ , ఆముదం , సోయా , ', '<p style="margin-left:-4.5pt"><span style="font-size:18px"><strong>క్రీడా ప్లాంటర్</strong></span></p>\r\n\r\n<p style="margin-left:-4.5pt">దీనితో రోజుకు రెండుఎకరాలలో వేరుశనగ, ఆముదాలు, మొక్కజొన్న, సోయా చిక్కుడు లాంటి పంటలు విత్తుకోవచ్చు</p>\r\n\r\n<p style="margin-left:-4.5pt">ఖరీదు 15౦౦ రూ.</p>\r\n\r\n<p style="margin-left:-4.5pt">ఎడ్లతో లాగబడే యంత్రం&nbsp;</p>\r\n', 'ytgqb79uux9spk4i5r5r', ''),
('mpuet2lqmptkrmthwh90', 'machines_rotavator_poster.jpg', 'Rotavator', 'రోటావెటర్', 'tillage', 'motor', 'd6w9z57rtt1hu5nm0svj+5msr3s5clh36mc9s6hev+qzzzf8al05d3ukawlk8y+', '', '<p><span style="font-size:18px"><strong>రోటావేటర్</strong></span></p>\r\n\r\n<p>ఇది మాగాణి&nbsp; దమ్ముకు, మెట్ట భూముల దుక్కికి, నేలల&nbsp; తయారికి బాగా పనికి వస్తుందిఇతర పరికరాలంటే&nbsp; నేల తయారీలో ఇది చక్కగా పని చేస్తుందని రైతుల అనుభవం.</p>\r\n\r\n<p>ఇది వాడుతున్నపుడు మొదట పొడి దుక్కి చేయాల్సిన అవసరం లేదు.</p>\r\n\r\n<p>ఇది 10 సెం.మీ లోతు వరకు నేలను కోసి నేలను దమ్ము చేస్తుంది.</p>\r\n\r\n<p>మెట్ట భూములలో ఒకేసారి దుక్కునప్పుడు, పెద్ద పెద్ద మట్టి గడ్డలను పొడి చేయడం, నేలను గుల్ల చేయడం, భూమిని చదును చేయడానికి ఈ యంత్రం ద్వారా అవకాసం వుంది.</p>\r\n\r\n<p>గత పంటల అవశేషాలను, మొక్కల కాండాలను&nbsp; వేర్లతో సహ&nbsp; పెకిలించి చిన్న చిన్న ముక్కలు చేసి మట్టిలో కలిసిపోయలా చేస్తుంది. దీనివల్ల భూసారం పెరుగుతుంది.</p>\r\n\r\n<p>పత్తి, చెరకు సాగుబడి భూములలో రోటా వీదర్ వాడి దుక్కి చేయడం మంచిది.</p>\r\n\r\n<p>రోటా వి టార్తో దమ్ము చేయటం వలన నీరు లోనికి ఇంకిపోకుండా 10సెం.మీ. లోతులో గట్టి పొర ఏర్పడుతుంది.</p>\r\n\r\n<p>ఎకరం నేలని రెండుసార్లు దమ్ము చేయడానికి 8 గంటల సమయం పడుతుంది.&nbsp;</p>\r\n\r\n<p>రోట వేటర్లు&nbsp;&nbsp;రకరకాల సైజులను&nbsp; లభిస్తాయి.</p>\r\n\r\n<p>వీటి వెడల్పును బట్టి ట్రాక్టర్ హెచ్ పి నిర్ణయించబడుతుంది</p>\r\n\r\n<p>౩-6 అంగుళాల వెడల్పు</p>\r\n\r\n<p>రోటా వీడర్ లాగడానికి 35-80 హెచ్ పి ట్రాక్టర్ అవసరం</p>\r\n\r\n<p>&nbsp;</p>\r\n', 'oyp7vzmki26yj4f56tx0', '');
