('gg7va4ckuw6zhz3wm9xx', '3m8o0vtf247qjxe8566k', '%E0%B0%B5%E0%B0%BF%E0%B0%A4%E0%B1%8D%E0%B0%A4%E0%B0%A8%20%E0%B0%B0%E0%B0%95%E0%B0%BE%E0%B0%B2%E0%B1%81', 2373, '<p><span style="font-size:18px"><strong><span style="font-family:gautami,sans-serif">అర్క అమూల్య</span></strong><span style="font-family:arial,sans-serif">&nbsp;</span><span style="font-family:arial,sans-serif">(</span><strong><span style="font-family:gautami,sans-serif">సీడ్ లెస్ జామ * అలహాబాద్ సఫేదా</span></strong><span style="font-family:arial,sans-serif">) : </span></span></p>\r\n\r\n<p><span style="font-family:gautami,sans-serif; font-size:10pt">కాయలు మధ్యస్తంగా</span><span style="font-family:arial,sans-serif; font-size:10pt">, </span><span style="font-family:gautami,sans-serif; font-size:10pt">తెల్లకండతో తియ్యగా వుంటాయి.</span></p>\r\n\r\n<p><span style="font-family:gautami,sans-serif; font-size:10pt">విత్తనాలు మెత్తగా వుంటాయి.ఎక్కువ రోజులు నిల్వ ఉంటాయి.</span></p>\r\n'),
('gg7va4ckuw6zhz3wm9xx', '3m8o0vtf247qjxe8566k', '%E0%B0%B5%E0%B0%BF%E0%B0%A4%E0%B1%8D%E0%B0%A4%E0%B0%A8%20%E0%B0%B0%E0%B0%95%E0%B0%BE%E0%B0%B2%E0%B1%81', 2374, '<p><span style="font-size:18px"><strong><span style="font-family:gautami,sans-serif">శ్వేత</span></strong><span style="font-family:arial,sans-serif">&nbsp;: </span></span></p>\r\n\r\n<p><span style="font-family:gautami,sans-serif; font-size:10pt">అధిక దిగుబడి నిచ్చే తెలుపు కండ రకం. </span></p>\r\n\r\n<p><span style="font-family:gautami,sans-serif; font-size:10pt">పండు సుమారు </span><span style="font-family:arial,sans-serif; font-size:10pt">225</span><span style="font-family:gautami,sans-serif; font-size:10pt">గ్రాములు బరువుతో తెలుపు రంగుతో ఎరుపు మచ్చలు కలిగి ఉంటుంది.</span></p>\r\n\r\n<p><span style="font-family:gautami,sans-serif; font-size:10pt">గింజలు మృదువుగా వుండి</span><span style="font-family:arial,sans-serif; font-size:10pt">, </span><span style="font-family:gautami,sans-serif; font-size:10pt">నాటిన ఆరవ సంవత్సరంలో సుమారుగా </span><span style="font-family:arial,sans-serif; font-size:10pt">1</span><span style="font-family:gautami,sans-serif; font-size:10pt">౦౦కిలోల దిగుబడి నిస్తుంది.</span></p>\r\n'),
('gg7va4ckuw6zhz3wm9xx', '3m8o0vtf247qjxe8566k', '%E0%B0%B5%E0%B0%BF%E0%B0%A4%E0%B1%8D%E0%B0%A4%E0%B0%A8%20%E0%B0%B0%E0%B0%95%E0%B0%BE%E0%B0%B2%E0%B1%81', 2375, '<p><span style="font-size:16px"><strong><span style="font-family:gautami,sans-serif">ఎర్రకండ రకాలు</span></strong><span style="font-family:arial,sans-serif">&nbsp;: </span></span></p>\r\n\r\n<p><span style="font-family:gautami,sans-serif; font-size:10pt">ఎర్రని గుజ్జుగల జామపండ్లు గుజ్జు పరిశ్రమలో వాడకం ఎక్కువుగా వున్నది.</span></p>\r\n\r\n<p><span style="font-size:18px"><strong><span style="font-family:gautami,sans-serif">రెడప్లష్ : </span></strong></span></p>\r\n\r\n<p><strong><span style="font-family:gautami,sans-serif; font-size:10pt">&nbsp;</span></strong><span style="font-family:gautami,sans-serif; font-size:10pt">కాయ ముదురు ఆకుపచ్చ రంగుతో గరుకుగా ఉండి</span><span style="font-family:arial,sans-serif; font-size:10pt">,</span><span style="font-family:gautami,sans-serif; font-size:10pt">గింజలు గట్టిగా ఉండి</span><span style="font-family:arial,sans-serif; font-size:10pt">,</span><span style="font-family:gautami,sans-serif; font-size:10pt">కండ ఎరుపు రంగుతో ఉంటుంది.</span></p>\r\n\r\n<p><span style="font-family:gautami,sans-serif; font-size:10pt">ఎకరాకు సంవత్సరానికి దిగుబడి </span><span style="font-family:arial,sans-serif; font-size:10pt">70-80 </span><span style="font-family:gautami,sans-serif; font-size:10pt">క్వింటాళ్ళు.</span></p>\r\n'),
('gg7va4ckuw6zhz3wm9xx', '3m8o0vtf247qjxe8566k', '%E0%B0%B5%E0%B0%BF%E0%B0%A4%E0%B1%8D%E0%B0%A4%E0%B0%A8%20%E0%B0%B0%E0%B0%95%E0%B0%BE%E0%B0%B2%E0%B1%81', 2376, '<p><span style="font-size:18px"><strong><span style="font-family:gautami,sans-serif">లలిత్ :</span></strong><strong><span style="font-family:arial,sans-serif">&nbsp;</span></strong></span></p>\r\n\r\n<p><span style="font-family:gautami,sans-serif; font-size:10pt">అలహాబాద్ సఫేదతో పోలిస్తే </span><span style="font-family:arial,sans-serif; font-size:10pt">24% </span><span style="font-family:gautami,sans-serif; font-size:10pt">దిగుబడి ఎక్కువ నిస్తుంది.</span></p>\r\n\r\n<p><span style="font-family:gautami,sans-serif; font-size:10pt">పండు బరువు </span><span style="font-family:arial,sans-serif; font-size:10pt">185</span><span style="font-family:gautami,sans-serif; font-size:10pt">గ్రా. నుండి </span><span style="font-family:arial,sans-serif; font-size:10pt">2</span><span style="font-family:gautami,sans-serif; font-size:10pt">౦౦గ్రా.ఉండి నిల్వ పదార్దాల తయారికి అనువైన ఎరుపు కండ రకం.</span></p>\r\n'),
('gg7va4ckuw6zhz3wm9xx', '3m8o0vtf247qjxe8566k', '%E0%B0%B5%E0%B0%BF%E0%B0%A4%E0%B1%8D%E0%B0%A4%E0%B0%A8%20%E0%B0%B0%E0%B0%95%E0%B0%BE%E0%B0%B2%E0%B1%81', 2377, '<p><span style="font-size:18px"><strong><span style="font-family:gautami,sans-serif">కిరణ్ (</span></strong><span style="font-family:gautami,sans-serif">కంసారి*పర్పల్</span><span style="font-family:arial,sans-serif">&nbsp; </span><span style="font-family:gautami,sans-serif">లోకల్)</span><strong><span style="font-family:arial,sans-serif">&nbsp;:</span></strong><span style="font-family:arial,sans-serif">&nbsp;</span></span></p>\r\n\r\n<p><span style="font-family:gautami,sans-serif; font-size:10pt">ఐ. ఐ. హెచ్.ఆర్. బెంగుళురు నుండి ఈ మద్యనే విడుదలైన రకం. </span></p>\r\n\r\n<p><span style="font-family:gautami,sans-serif; font-size:10pt">పండు ఎరుపు కండతో మృదువైన గింజలున్న రకం. </span><span style="font-family:arial,sans-serif; font-size:10pt">&ldquo;</span><span style="font-family:gautami,sans-serif; font-size:10pt">లైకోపిన్</span><span style="font-family:arial,sans-serif; font-size:10pt">&rdquo; </span><span style="font-family:gautami,sans-serif; font-size:10pt">అధికంగా ఉంటుంది. జామ పల్ప్ తయారికి అనువైనది.</span></p>\r\n'),
