INSERT INTO `weather_forecast` (`data_entry_date`, `date`, `rainfall_description`, `temp_min_min`, `temp_min_max`, `temp_max_min`, `temp_max_max`, `wind_min_min`, `wind_min_max`, `wind_max_min`, `wind_max_max`, `other_info`) VALUES
('2014-06-12', '2014-06-12', 'sunny', 25, 32, 32, 44, 0, 0, 0, 0, '<p>రాబోవు 24 గంటలలో మహబూబ్నగర్ జిలలలో తేలిక పాటి వర్షాలు కురిసే అవకాశం ఉంది</p>\r\n'),
('2014-07-09', '2014-07-08', 'cloudy', 24, 25, 26, 34, 15, 20, 15, 20, '<p>గడచిన మూడురోజుల్లో జిల్లలో అక్కడకడ తేలికపాటి నుండి ఓ మోస్తరు వర్షాలు కురిసాయి . పగటి ఉష్ణోగ్రతలు 37 నుండి 39 డిగ్రీల సేల్సియుస్ మధ్య మరియు రాత్రి ఉష్ణోగ్రతలు 24 నుండి 25 డిగ్రీల సేల్సియుస్ మద్య ఉంటాయి . హైదరాబాద్ వాతావరణ కేంద్రం వారు అందించిన సమాచారం ప్రకారం రాబోవు ఇదురోజుల్లో జిల్లాలో అక్కడక్కడ తేలికపాటి వర్శాలు కురిసే అవకాశం ఉంది . నైరుతి దిశ నుండి గాలులు గంటకు 15 నుండి 20 కి. మీ. వేగంతో వియవచ్చు . పగటి ఉష్నోగ్రతలు 26 నుండి 34 డిగ్రీల సేల్సియుస్ మద్య మరియు రాత్రి ఉష్ణోగ్రతలు 24 నుండి 25 డిగ్రీల సేల్సియుస్ మద్య ఉండవచ్చు. &nbsp;</p>\r\n'),
('2014-07-09', '2014-07-09', 'cloudy', 24, 25, 26, 34, 15, 20, 15, 20, '<p>హైదరాబాద్ వాతావరణ కేంద్రం వారు అందించిన సమాచారం ప్రకారం రాబోవు ఇదురోజుల్లో జిల్లాలో అక్కడక్కడ తేలికపాటి వర్శాలు కురిసే అవకాశం ఉంది . నైరుతి దిశ నుండి గాలులు గంటకు 15 నుండి 20 కి. మీ. వేగంతో వియవచ్చు . పగటి ఉష్నోగ్రతలు 26 నుండి 34 డిగ్రీల సేల్సియుస్ మద్య మరియు రాత్రి ఉష్ణోగ్రతలు 24 నుండి 25 డిగ్రీల సేల్సియుస్ మద్య ఉండవచ్చు. &nbsp;</p>\r\n'),
('2015-01-02', '2015-01-02', 'cloudy', 24, 26, 32, 34, 8, 9, 8, 9, '<p><span style="font-size:24px">గడిచిన&nbsp;</span></p>\r\n'),
('2015-01-12', '2015-01-12', 'sunny', 10, 13, 28, 30, 9, 9, 13, 13, '<p>రాబోవు ఐదు రోజులలో వాతావరణం పొడిగా ఉంటుంది. గాలి వేగం 9-13 కి. మీ ఆగ్నేయ దిశ నుండి , పగటి ఉష్ణోగ్రతలు 28-30, రాత్రి ఉష్ణోగ్రతలు 10-13 డిగ్రీల సెల్సియస్ మద్య నమోదు కావచ్చు&nbsp;</p>\r\n'),
('2015-06-26', '2015-06-26', 'cloudy', 23, 25, 32, 35, 15, 20, 0, 0, '<p>రాగల 5 రోజులలో ఆకాశం&nbsp;&nbsp;మేఘవ్రుత్తమై ఉంటుంది. ఉష్ణోగ్రతలు ; పగలు 32-35 , రాత్రి 23 - 25 డి సే .మద్య నమోదు కావచ్చు&nbsp;&nbsp;</p>\r\n'),
